import React, { useState, useEffect } from 'react';
import './App.css';
import { FaPlus, FaTrash, FaCheckCircle, FaClock, FaListAlt } from 'react-icons/fa';
import Notification from './components/Notification';

// –î–æ–±–∞–≤—å—Ç–µ —ç—Ç–æ—Ç —Ö—É–∫ –¥–ª—è –∞–Ω–∏–º–∞—Ü–∏–π
const useHoverAnimation = () => {
  const [isHovered, setIsHovered] = useState(false);

  const hoverProps = {
    onMouseEnter: () => setIsHovered(true),
    onMouseLeave: () => setIsHovered(false),
    style: {
      transform: isHovered ? 'scale(1.02)' : 'scale(1)',
      transition: 'transform 0.2s ease'
    }
  };

  return hoverProps;
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç TodoItem
const TodoItem = ({ todo, onToggle, onDelete, onEdit }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(todo.text);

  const handleSave = () => {
    if (editText.trim()) {
      onEdit(todo.id, editText);
      setIsEditing(false);
    }
  };

  const handleCancel = () => {
    setEditText(todo.text);
    setIsEditing(false);
  };

  // –í —Ñ—É–Ω–∫—Ü–∏–∏ handleDeleteTodo –¥–æ–±–∞–≤—å—Ç–µ –∞–Ω–∏–º–∞—Ü–∏—é:
const handleDeleteTodo = (id) => {
  const todoElement = document.querySelector(`[data-todo-id="${id}"]`);
  if (todoElement) {
    todoElement.style.animation = 'slideOut 0.3s ease forwards';
    setTimeout(() => {
      setTodos(todos.filter(todo => todo.id !== id));
    }, 300);
  }
};

// –í —Ä–µ–Ω–¥–µ—Ä–µ todo –¥–æ–±–∞–≤—å—Ç–µ data-todo-id:
<div 
  key={todo.id} 
  data-todo-id={todo.id}
  className={`todo-item ${todo.completed ? 'completed' : ''}`}
></div>

  return (
    <div className={`todo-item ${todo.completed ? 'completed' : ''}`}>
      <div 
        className={`checkbox ${todo.completed ? 'checked' : ''}`}
        onClick={() => onToggle(todo.id)}
      >
        {todo.completed && <span>‚úì</span>}
      </div>

      {isEditing ? (
        <div style={{ display: 'flex', flex: 1, gap: '10px' }}>
          <input
            type="text"
            value={editText}
            onChange={(e) => setEditText(e.target.value)}
            style={{
              flex: 1,
              padding: '8px 12px',
              border: '2px solid #4361ee',
              borderRadius: '8px',
              fontSize: '1rem'
            }}
            autoFocus
            onKeyPress={(e) => e.key === 'Enter' && handleSave()}
          />
          <button onClick={handleSave} style={{ 
            background: '#2ec4b6', 
            color: 'white', 
            border: 'none', 
            padding: '8px 15px',
            borderRadius: '8px',
            cursor: 'pointer'
          }}>
            –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
          </button>
          <button onClick={handleCancel} style={{ 
            background: '#8d99ae', 
            color: 'white', 
            border: 'none', 
            padding: '8px 15px',
            borderRadius: '8px',
            cursor: 'pointer'
          }}>
            –û—Ç–º–µ–Ω–∞
          </button>
        </div>
      ) : (
        <>
          <span 
            style={{ 
              flex: 1, 
              textDecoration: todo.completed ? 'line-through' : 'none',
              opacity: todo.completed ? 0.7 : 1,
              cursor: 'pointer'
            }}
            onDoubleClick={() => setIsEditing(true)}
          >
            {todo.text}
          </span>
          <div style={{ display: 'flex', gap: '10px' }}>
            <button 
              onClick={() => setIsEditing(true)}
              style={{ 
                background: 'transparent', 
                border: 'none', 
                color: '#4361ee',
                cursor: 'pointer',
                fontSize: '16px'
              }}
            >
              ‚úèÔ∏è
            </button>
            <button 
              onClick={() => onDelete(todo.id)}
              style={{ 
                background: 'transparent', 
                border: 'none', 
                color: '#e71d36',
                cursor: 'pointer',
                fontSize: '16px'
              }}
            >
              üóëÔ∏è
            </button>
          </div>
        </>
      )}
    </div>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç AddTodoForm
const AddTodoForm = ({ onAdd }) => {
  const [text, setText] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (text.trim()) {
      onAdd(text);
      setText('');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="–ß—Ç–æ –Ω—É–∂–Ω–æ —Å–¥–µ–ª–∞—Ç—å?"
          style={{
            flex: 1,
            padding: '12px 16px',
            border: '2px solid #ddd',
            borderRadius: '8px',
            fontSize: '1rem'
          }}
        />
        <button 
          type="submit"
          style={{
            background: '#4361ee',
            color: 'white',
            border: 'none',
            padding: '12px 24px',
            borderRadius: '8px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}
        >
          <FaPlus /> –î–æ–±–∞–≤–∏—Ç—å
        </button>
      </div>
      <div style={{ color: '#666', fontSize: '0.9rem' }}>
        üí° –ù–∞–∂–º–∏—Ç–µ Enter –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è
      </div>
    </form>
  );
};

// –ì–ª–∞–≤–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç App
function App() {
  cconst [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [filter, setFilter] = useState('all');
  const [theme, setTheme] = useState('light');

  const [notification, setNotification] = useState(null);

  // –ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑ localStorage
  useEffect(() => {
    const saved = localStorage.getItem('react-todos');
    if (saved) {
      setTodos(JSON.parse(saved));
    }
  }, []);

  // –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –≤ localStorage
  useEffect(() => {
    localStorage.setItem('react-todos', JSON.stringify(todos));
  }, [todos]);

  // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –∑–∞–¥–∞—á–∏
 const handleAddTodo = (text) => {
  if (input.trim() === '') {
    showNotification('–í–≤–µ–¥–∏—Ç–µ —Ç–µ–∫—Å—Ç –∑–∞–¥–∞—á–∏!', 'warning');
    return;
  }

  const newTodo = {
    id: Date.now(),
    text: input.trim(),
    completed: false,
    createdAt: new Date().toISOString()
  };

  setTodos([newTodo, ...todos]);
  setInput('');

  // –ü–û–ö–ê–ó–´–í–ê–ï–ú –£–í–ï–î–û–ú–õ–ï–ù–ò–ï
  showNotification('‚úÖ –ó–∞–¥–∞—á–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∞!', 'success');
};

  // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ —Å—Ç–∞—Ç—É—Å–∞
  const handleToggleTodo = (id) => {
  const todo = todos.find(t => t.id === id);
  const isCompleting = !todo.completed;

  setTodos(todos.map(todo =>
    todo.id === id ? { ...todo, completed: !todo.completed } : todo
  ));

  // –ü–û–ö–ê–ó–´–í–ê–ï–ú –£–í–ï–î–û–ú–õ–ï–ù–ò–ï
  const message = isCompleting 
    ? '‚úÖ –ó–∞–¥–∞—á–∞ –≤—ã–ø–æ–ª–Ω–µ–Ω–∞!' 
    : '‚Ü©Ô∏è –ó–∞–¥–∞—á–∞ –≤–æ–∑–≤—Ä–∞—â–µ–Ω–∞ –≤ –∞–∫—Ç–∏–≤–Ω—ã–µ';
  showNotification(message, 'info');
};

  // –£–¥–∞–ª–µ–Ω–∏–µ –∑–∞–¥–∞—á–∏
  const handleDeleteTodo = (id) => {
  const taskText = todos.find(t => t.id === id)?.text || '–∑–∞–¥–∞—á–∞';

  // –ï—Å–ª–∏ —Ö–æ—Ç–∏—Ç–µ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ:
  if (window.confirm(`–£–¥–∞–ª–∏—Ç—å –∑–∞–¥–∞—á—É "${taskText}"?`)) {
    setTodos(todos.filter(todo => todo.id !== id));

    // –ü–û–ö–ê–ó–´–í–ê–ï–ú –£–í–ï–î–û–ú–õ–ï–ù–ò–ï
    showNotification('üóëÔ∏è –ó–∞–¥–∞—á–∞ —É–¥–∞–ª–µ–Ω–∞', 'warning');
  }
};

  // –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –∑–∞–¥–∞—á–∏
  const handleEditTodo = (id, newText) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, text: newText.trim() } : todo
    ));
  };

  // –û—á–∏—Å—Ç–∫–∞ –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã—Ö
  const handleClearCompleted = () => {
    if (window.confirm('–£–¥–∞–ª–∏—Ç—å –≤—Å–µ –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ –∑–∞–¥–∞—á–∏?')) {
      setTodos(todos.filter(todo => !todo.completed));
    }
  };

  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è
  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  // –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
  const stats = {
    total: todos.length,
    active: todos.filter(t => !t.completed).length,
    completed: todos.filter(t => t.completed).length
  };

  return (
    <div className="App">
      <div className="container">
        <header className="header">
          <h1>‚úÖ Todo App</h1>
          <p className="subtitle">–£–ø—Ä–∞–≤–ª—è–π—Ç–µ –∑–∞–¥–∞—á–∞–º–∏ —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ</p>
        </header>

        <main className="main-content">
          <AddTodoForm onAdd={handleAddTodo} />

          {/* –§–∏–ª—å—Ç—Ä—ã */}
          <div style={{ 
            display: 'flex', 
            gap: '10px', 
            marginBottom: '20px',
            flexWrap: 'wrap'
          }}>
            {['all', 'active', 'completed'].map(f => (
              <button
                key={f}
                onClick={() => setFilter(f)}
                style={{
                  padding: '8px 16px',
                  border: `2px solid ${filter === f ? '#4361ee' : '#ddd'}`,
                  background: filter === f ? '#4361ee' : 'white',
                  color: filter === f ? 'white' : '#333',
                  borderRadius: '6px',
                  cursor: 'pointer'
                }}
              >
                {f === 'all' ? '–í—Å–µ' : f === 'active' ? '–ê–∫—Ç–∏–≤–Ω—ã–µ' : '–í—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ'}
                <span style={{ 
                  marginLeft: '8px',
                  background: filter === f ? 'rgba(255,255,255,0.2)' : '#f0f0f0',
                  padding: '2px 8px',
                  borderRadius: '10px',
                  fontSize: '0.8rem'
                }}>
                  {f === 'all' ? stats.total : f === 'active' ? stats.active : stats.completed}
                </span>
              </button>
            ))}
          </div>

          {/* –°–ø–∏—Å–æ–∫ –∑–∞–¥–∞—á */}
          <div style={{ minHeight: '200px' }}>
            {filteredTodos.length === 0 ? (
              <div style={{ 
                textAlign: 'center', 
                padding: '40px 20px', 
                color: '#666' 
              }}>
                <p style={{ fontSize: '1.2rem' }}>
                  {filter === 'all' ? '–ù–µ—Ç –∑–∞–¥–∞—á. –î–æ–±–∞–≤—å—Ç–µ –ø–µ—Ä–≤—É—é!' :
                   filter === 'active' ? '–ù–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö –∑–∞–¥–∞—á!' :
                   '–ù–µ—Ç –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã—Ö –∑–∞–¥–∞—á!'}
                </p>
              </div>
            ) : (
              filteredTodos.map(todo => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onToggle={handleToggleTodo}
                  onDelete={handleDeleteTodo}
                  onEdit={handleEditTodo}
                />
              ))
            )}
          </div>

          {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
          <div style={{ 
            marginTop: '30px', 
            padding: '20px', 
            background: '#f8f9fa', 
            borderRadius: '10px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            flexWrap: 'wrap',
            gap: '15px'
          }}>
            <div style={{ display: 'flex', gap: '30px', flexWrap: 'wrap' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <FaListAlt style={{ color: '#4361ee', fontSize: '20px' }} />
                <div>
                  <div style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{stats.total}</div>
                  <div style={{ fontSize: '0.9rem', color: '#666' }}>–í—Å–µ–≥–æ</div>
                </div>
              </div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <FaClock style={{ color: '#4361ee', fontSize: '20px' }} />
                <div>
                  <div style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{stats.active}</div>
                  <div style={{ fontSize: '0.9rem', color: '#666' }}>–ê–∫—Ç–∏–≤–Ω—ã—Ö</div>
                </div>
              </div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <FaCheckCircle style={{ color: '#4361ee', fontSize: '20px' }} />
                <div>
                  <div style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{stats.completed}</div>
                  <div style={{ fontSize: '0.9rem', color: '#666' }}>–í—ã–ø–æ–ª–Ω–µ–Ω–æ</div>
                </div>
              </div>
            </div>

            <button
              onClick={handleClearCompleted}
              disabled={stats.completed === 0}
              style={{
                background: stats.completed === 0 ? '#f0f0f0' : 'white',
                color: stats.completed === 0 ? '#999' : '#e71d36',
                border: `2px solid ${stats.completed === 0 ? '#ddd' : '#e71d36'}`,
                padding: '10px 20px',
                borderRadius: '6px',
                cursor: stats.completed === 0 ? 'not-allowed' : 'pointer',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}
            >
              <FaTrash /> –û—á–∏—Å—Ç–∏—Ç—å –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ
            </button>
          </div>
        </main>

        <footer style={{ 
          padding: '20px', 
          textAlign: 'center', 
          background: '#f8f9fa', 
          borderTop: '1px solid #ddd',
          marginTop: '30px'
        }}>
          <div>
            <span style={{ color: '#4361ee', fontWeight: 'bold' }}>React</span> ‚Ä¢ 
            <span style={{ color: '#7209b7', fontWeight: 'bold', margin: '0 10px' }}>Hooks</span> ‚Ä¢ 
            <span style={{ color: '#2ec4b6', fontWeight: 'bold' }}>LocalStorage</span>
          </div>
          <div style={{ marginTop: '10px', color: '#666', fontSize: '0.9rem' }}>
            Heathcliff ‚Ä¢ {new Date().getFullYear()}
          </div>
        </footer>
      </div>
    </div>
  );
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–∫–∞–∑–∞ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π
const showNotification = (message, type = 'info') => {
  setNotification({ message, type });
};

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–∫—Ä—ã—Ç–∏—è —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π
const hideNotification = () => {
  setNotification(null);
};
export default App;
